import React from 'react';
import { Input, Button, Avatar } from 'antd';
import { SendOutlined, UserOutlined } from '@ant-design/icons';
import './ChatInterface.css';

interface Message {
  id: number;
  content: string;
  type: 'user' | 'ai';
  timestamp: Date;
}

export default function ChatInterface() {
  const [messages, setMessages] = React.useState([
    {
      id: 1,
      content: '您好！欢迎访问美美生活！我们专注于为中国客户提供美国公司注册与税务申报服务。请问有什么可以帮助您的吗？',
      type: 'ai',
      timestamp: new Date(Date.now() - 600000),
    },
    {
      id: 2,
      content: '我想了解在美国注册公司的流程和费用。',
      type: 'user',
      timestamp: new Date(Date.now() - 540000),
    },
    {
      id: 3,
      content: '美国公司注册流程主要包括：选择公司类型（LLC或C-Corp）、确定注册州、提交申请材料、获取EIN税号等步骤。基础套餐费用从$299起，根据不同州和服务内容有所差异。',
      type: 'ai',
      timestamp: new Date(Date.now() - 520000),
    },
    {
      id: 4,
      content: '哪个州注册比较好？',
      type: 'user',
      timestamp: new Date(Date.now() - 480000),
    },
    {
      id: 5,
      content: '常见的热门州包括特拉华州、怀俄明州、内华达州和佛罗里达州等。特拉华州对国际客户友好，税制优惠；怀俄明州无州所得税；内华达州保密性好；佛罗里达州无个人所得税。您可以根据业务需求选择适合的州。',
      type: 'ai',
      timestamp: new Date(Date.now() - 460000),
    },
    {
      id: 6,
      content: '我需要亲自去美国办理吗？',
      type: 'user',
      timestamp: new Date(Date.now() - 400000),
    },
    {
      id: 7,
      content: '不需要。我们提供全程远程服务，您无需亲自前往美国。所有流程均可在线完成，包括公司注册、EIN税号申请、银行开户辅助等。我们会为您准备所有必要文件，并指导您完成整个过程。',
      type: 'ai',
      timestamp: new Date(Date.now() - 380000),
    },
    {
      id: 8,
      content: '我们提供全方位的美国公司注册服务，包括公司注册、税号申请、银行开户、税务申报等。您可以在右侧选择您需要注册的州和服务类型，获取实时报价。',
      type: 'ai',
      timestamp: new Date(Date.now() - 30000),
    },
  ]);
  const [inputValue, setInputValue] = React.useState('');
  const messagesEndRef = React.useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  React.useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSend = () => {
    if (inputValue.trim() === '') return;

    // 添加用户消息
    const userMessage = {
      id: messages.length + 1,
      content: inputValue,
      type: 'user',
      timestamp: new Date(),
    };

    setMessages((prev) => [...prev, userMessage]);
    setInputValue('');

    // 模拟AI回复
    setTimeout(() => {
      const aiResponse = {
        id: messages.length + 2,
        content: getAIResponse(inputValue),
        type: 'ai',
        timestamp: new Date(),
      };
      setMessages((prev) => [...prev, aiResponse]);
    }, 1000);
  };

  const getAIResponse = (query) => {
    // 简单的模拟回复，实际应用中应该连接到真实的AI服务
    const responses = {
      'default': '感谢您的咨询。我们专注于为中国客户提供美国公司注册与税务申报服务。请问您有具体的问题吗？',
      '费用': '我们的基础注册套餐从$299起，具体费用取决于注册州、地址类型等因素。您可以使用上方的计算器获取精确报价。',
      '时间': '一般情况下，美国公司注册流程需要7-10个工作日完成。如需加急服务，请联系我们的客服。',
      '流程': '注册流程包括：选择公司类型、确定注册州、提交申请材料、获取EIN税号、开设银行账户等。我们将全程协助您完成这些步骤。',
      '材料': '您需要提供有效身份证件、联系方式等基本信息。对于特定行业可能需要额外资质文件。',
      '税务': '美国公司需要按时进行税务申报，包括联邦税、州税和地方税。我们提供全面的税务咨询和申报服务。',
    };

    for (const key in responses) {
      if (query.includes(key)) {
        return responses[key];
      }
    }

    return responses['default'];
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSend();
    }
  };

  return (
    <div className="chat-interface" style={{ padding: 0, margin: 0 }}>
      <div className="chat-history" style={{ padding: 0, margin: 0 }}>
        {messages.map((message) => (
          <div
            key={message.id}
            className={`message ${message.type === 'user' ? 'user-message' : 'ai-message'}`}
            style={{ padding: 0, margin: '0 0 8px 0' }}
          >
            <div className="message-avatar" style={{ 
              padding: 0, 
              margin: message.type === 'ai' ? '4px 4px 0 2px' : '4px 2px 0 4px' 
            }}>
              {message.type === 'user' ? (
                <Avatar icon={<UserOutlined />} size="small" />
              ) : (
                <div style={{ 
                  backgroundColor: '#1a73e8',
                  fontSize: '10px',
                  width: '20px',
                  height: '20px',
                  borderRadius: '50%',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  color: 'white',
                  marginLeft: 0
                }}>AI</div>
              )}
            </div>
            <div className="message-content" style={{ padding: 0, margin: 0 }}>
              <div className="message-bubble" style={{ margin: 0 }}>
                {message.content}
              </div>
              <div className="message-time" style={{ margin: '4px 0 0 0', padding: 0 }}>
                {message.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
              </div>
            </div>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>
      <div className="chat-input">
        <Input
          placeholder="请输入您的问题..."
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          onKeyPress={handleKeyPress}
          style={{ width: '100%', paddingLeft: '12px' }}
          suffix={
            <Button
              type="primary"
              shape="circle"
              icon={<SendOutlined />}
              onClick={handleSend}
              disabled={inputValue.trim() === ''}
              size="small"
            />
          }
        />
      </div>
    </div>
  );
}
