import React, { useState, useEffect } from 'react';
import { Card, Form, Select, Radio, Space, Input, Typography, Button, Dropdown, Menu, DatePicker } from 'antd';
import { DownOutlined, UserOutlined, MailOutlined, PhoneOutlined, BankOutlined } from '@ant-design/icons';
import { CompanyInfo } from '../App';
import './Calculator.css';

const { Option } = Select;
const { Title, Text } = Typography;
const { TextArea } = Input;

interface PriceData {
  basePrice: number;
  addressPrices: {
    virtual: number;
    physical: number;
  };
  boiPrice: number;
  sealPrice: number;
  stateTaxes: Record<string, number>;
}

interface CalculatorProps {
  companyInfo: CompanyInfo;
  updateCompanyInfo: (info: Partial<CompanyInfo>) => void;
}

const Calculator: React.FC<CalculatorProps> = ({ companyInfo, updateCompanyInfo }) => {
  const [form] = Form.useForm();
  const [totalPrice, setTotalPrice] = useState(0);
  const [selectedState, setSelectedState] = useState(companyInfo.state || 'Colorado');
  const [selectedAddressType, setSelectedAddressType] = useState('virtual');
  const [needBoi, setNeedBoi] = useState(false);
  const [needSeal, setNeedSeal] = useState(false);

  // 模拟价格数据
  const priceData: PriceData = {
    basePrice: 299,
    addressPrices: {
      virtual: 99,
      physical: 199,
    },
    boiPrice: 99,
    sealPrice: 49,
    stateTaxes: {
      'Delaware': 90,
      'Wyoming': 50,
      'Nevada': 75,
      'Florida': 125,
      'California': 800,
      'New York': 200,
      'Colorado': 150,
    }
  };

  const calculatePrice = () => {
    let price = priceData.basePrice;
    price += priceData.stateTaxes[selectedState] || 0;
    price += priceData.addressPrices[selectedAddressType as 'virtual' | 'physical'];
    
    if (needBoi) {
      price += priceData.boiPrice;
    }
    
    if (needSeal) {
      price += priceData.sealPrice;
    }
    
    setTotalPrice(price);
  };

  useEffect(() => {
    calculatePrice();
    // 更新App组件中的状态
    updateCompanyInfo({ state: selectedState });
  }, [selectedState, selectedAddressType, needBoi, needSeal, updateCompanyInfo]);

  // 状态选择下拉菜单
  const stateMenu = (
    <Menu 
      onClick={(e) => {
        setSelectedState(e.key);
      }}
      selectedKeys={[selectedState]}
    >
      <Menu.Item key="Delaware">特拉华州 (Delaware)</Menu.Item>
      <Menu.Item key="Wyoming">怀俄明州 (Wyoming)</Menu.Item>
      <Menu.Item key="Nevada">内华达州 (Nevada)</Menu.Item>
      <Menu.Item key="Florida">佛罗里达州 (Florida)</Menu.Item>
      <Menu.Item key="California">加利福尼亚州 (California)</Menu.Item>
      <Menu.Item key="New York">纽约州 (New York)</Menu.Item>
      <Menu.Item key="Colorado">科罗拉多州 (Colorado)</Menu.Item>
    </Menu>
  );

  // 地址类型下拉菜单
  const addressTypeMenu = (
    <Menu 
      onClick={(e) => {
        setSelectedAddressType(e.key);
      }}
      selectedKeys={[selectedAddressType]}
    >
      <Menu.Item key="virtual">虚拟地址</Menu.Item>
      <Menu.Item key="physical">实体地址</Menu.Item>
    </Menu>
  );

  // 获取状态显示名称
  const getStateDisplayName = (key) => {
    const stateMap = {
      'Delaware': '特拉华州 (Delaware)',
      'Wyoming': '怀俄明州 (Wyoming)',
      'Nevada': '内华达州 (Nevada)',
      'Florida': '佛罗里达州 (Florida)',
      'California': '加利福尼亚州 (California)',
      'New York': '纽约州 (New York)',
      'Colorado': '科罗拉多州 (Colorado)'
    };
    return stateMap[key] || key;
  };

  // 获取地址类型显示名称
  const getAddressTypeDisplayName = (key) => {
    return key === 'virtual' ? '虚拟地址' : '实体地址';
  };

  // 处理客户信息表单变化
  const handleFormValuesChange = (changedValues, allValues) => {
    updateCompanyInfo({
      companyName: allValues.companyName,
      ownerName: allValues.customerName,
      phone: allValues.phone,
      email: allValues.email,
      address: allValues.address,
      businessScope: allValues.businessScope
    });
  };

  return (
    <div className="calculator-container">
      <Card className="calculator-card">
        <div className="calculator-header">
          <Title level={4}>公司注册选项</Title>
        </div>
        <div className="calculator-body">
          <div className="calculator-options">
            <div className="option-item">
              <span className="option-label">注册州：</span>
              <Dropdown overlay={stateMenu} trigger={['click']}>
                <Button className="dropdown-button">
                  {getStateDisplayName(selectedState)} <DownOutlined />
                </Button>
              </Dropdown>
            </div>
            
            <div className="option-item">
              <span className="option-label">地址类型：</span>
              <Dropdown overlay={addressTypeMenu} trigger={['click']}>
                <Button className="dropdown-button">
                  {getAddressTypeDisplayName(selectedAddressType)} <DownOutlined />
                </Button>
              </Dropdown>
            </div>
            
            <div className="option-item">
              <span className="option-label">需要BOI申报：</span>
              <Radio.Group 
                options={[
                  { label: '是', value: true },
                  { label: '否', value: false },
                ]}
                optionType="button"
                buttonStyle="solid"
                value={needBoi}
                onChange={(e) => setNeedBoi(e.target.value)}
              />
            </div>
            
            <div className="option-item">
              <span className="option-label">需要纸质印章：</span>
              <Radio.Group 
                options={[
                  { label: '是', value: true },
                  { label: '否', value: false },
                ]}
                optionType="button"
                buttonStyle="solid"
                value={needSeal}
                onChange={(e) => setNeedSeal(e.target.value)}
              />
            </div>
          </div>
          
          <div className="calculator-result">
            <Card className="price-card">
              <Title level={4}>费用总计</Title>
              <div className="price-amount">
                <span className="currency">$</span>
                <span className="amount">{totalPrice}</span>
              </div>
              <Text type="secondary">包含全套公司注册服务</Text>
            </Card>
          </div>
        </div>
      </Card>
      
      <Card className="customer-info-card">
        <div className="card-header">
          <Title level={4}>客户基本信息</Title>
        </div>
        <Form 
          layout="vertical"
          initialValues={{
            companyName: companyInfo.companyName,
            customerName: companyInfo.ownerName,
            email: companyInfo.email,
            phone: companyInfo.phone,
            address: companyInfo.address,
            businessScope: companyInfo.businessScope
          }}
          onValuesChange={handleFormValuesChange}
        >
          <div className="form-row">
            <Form.Item 
              name="customerName" 
              label="客户姓名" 
              rules={[{ required: true, message: '请输入姓名' }]}
              className="form-item-half"
            >
              <Input prefix={<UserOutlined />} placeholder="请输入您的姓名" />
            </Form.Item>
            
            <Form.Item 
              name="phone" 
              label="联系电话"
              rules={[{ required: true, message: '请输入联系电话' }]}
              className="form-item-half"
            >
              <Input prefix={<PhoneOutlined />} placeholder="请输入您的联系电话" />
            </Form.Item>
          </div>
          
          <div className="form-row">
            <Form.Item 
              name="email" 
              label="电子邮箱"
              rules={[
                { required: true, message: '请输入电子邮箱' },
                { type: 'email', message: '请输入有效的邮箱地址' }
              ]}
              className="form-item-half"
            >
              <Input prefix={<MailOutlined />} placeholder="请输入您的电子邮箱" />
            </Form.Item>
            
            <Form.Item 
              name="wechat" 
              label="微信号"
              className="form-item-half"
            >
              <Input placeholder="请输入您的微信号（选填）" />
            </Form.Item>
          </div>
          
          <Form.Item 
            name="companyName" 
            label="拟注册公司名称"
            rules={[{ required: true, message: '请输入拟注册的公司名称' }]}
          >
            <Input prefix={<BankOutlined />} placeholder="请输入拟注册的公司名称" />
          </Form.Item>
          
          <Form.Item 
            name="address" 
            label="注册地址"
            rules={[{ required: true, message: '请输入注册地址' }]}
          >
            <Input placeholder="请输入注册地址" />
          </Form.Item>
          
          <Form.Item 
            name="businessScope" 
            label="经营范围"
          >
            <TextArea rows={3} placeholder="请简要描述公司的经营范围" />
          </Form.Item>
          
          <Form.Item>
            <Button type="primary" htmlType="submit" block>
              提交信息
            </Button>
          </Form.Item>
        </Form>
      </Card>
    </div>
  );
};

export default Calculator;
