import React, { useState, useEffect } from 'react';
import { Card, Form, Select, Radio, Space, InputNumber, Typography, Button, Dropdown, Menu } from 'antd';
import type { RadioChangeEvent } from 'antd';
import { DownOutlined } from '@ant-design/icons';
import './Calculator.css';

const { Option } = Select;
const { Title, Text } = Typography;

interface PriceData {
  basePrice: number;
  addressPrices: {
    virtual: number;
    physical: number;
  };
  boiPrice: number;
  sealPrice: number;
  stateTaxes: Record<string, number>;
}

const Calculator = () => {
  const [form] = Form.useForm();
  const [totalPrice, setTotalPrice] = useState(0);
  const [selectedState, setSelectedState] = useState('Delaware');
  const [selectedAddressType, setSelectedAddressType] = useState('virtual');
  const [needBoi, setNeedBoi] = useState(false);
  const [needSeal, setNeedSeal] = useState(false);

  // 模拟价格数据
  const priceData: PriceData = {
    basePrice: 299,
    addressPrices: {
      virtual: 99,
      physical: 199,
    },
    boiPrice: 99,
    sealPrice: 49,
    stateTaxes: {
      'Delaware': 90,
      'Wyoming': 50,
      'Nevada': 75,
      'Florida': 125,
      'California': 800,
      'New York': 200,
    }
  };

  const calculatePrice = () => {
    let price = priceData.basePrice;
    price += priceData.stateTaxes[selectedState] || 0;
    price += priceData.addressPrices[selectedAddressType as 'virtual' | 'physical'];
    
    if (needBoi) {
      price += priceData.boiPrice;
    }
    
    if (needSeal) {
      price += priceData.sealPrice;
    }
    
    setTotalPrice(price);
  };

  useEffect(() => {
    calculatePrice();
  }, [selectedState, selectedAddressType, needBoi, needSeal]);

  // 状态选择下拉菜单
  const stateMenu = (
    <Menu 
      onClick={(e) => {
        setSelectedState(e.key);
      }}
      selectedKeys={[selectedState]}
    >
      <Menu.Item key="Delaware">特拉华州 (Delaware)</Menu.Item>
      <Menu.Item key="Wyoming">怀俄明州 (Wyoming)</Menu.Item>
      <Menu.Item key="Nevada">内华达州 (Nevada)</Menu.Item>
      <Menu.Item key="Florida">佛罗里达州 (Florida)</Menu.Item>
      <Menu.Item key="California">加利福尼亚州 (California)</Menu.Item>
      <Menu.Item key="New York">纽约州 (New York)</Menu.Item>
    </Menu>
  );

  // 地址类型下拉菜单
  const addressTypeMenu = (
    <Menu 
      onClick={(e) => {
        setSelectedAddressType(e.key);
      }}
      selectedKeys={[selectedAddressType]}
    >
      <Menu.Item key="virtual">虚拟地址</Menu.Item>
      <Menu.Item key="physical">实体地址</Menu.Item>
    </Menu>
  );

  // 获取状态显示名称
  const getStateDisplayName = (key) => {
    const stateMap = {
      'Delaware': '特拉华州 (Delaware)',
      'Wyoming': '怀俄明州 (Wyoming)',
      'Nevada': '内华达州 (Nevada)',
      'Florida': '佛罗里达州 (Florida)',
      'California': '加利福尼亚州 (California)',
      'New York': '纽约州 (New York)'
    };
    return stateMap[key] || key;
  };

  // 获取地址类型显示名称
  const getAddressTypeDisplayName = (key) => {
    return key === 'virtual' ? '虚拟地址' : '实体地址';
  };

  return (
    <Card className="calculator-card">
      <div className="calculator-header">
        <Title level={4}>公司注册费用计算器</Title>
      </div>
      <div className="calculator-body">
        <div className="calculator-options">
          <div className="option-item">
            <span className="option-label">注册州：</span>
            <Dropdown overlay={stateMenu} trigger={['click']}>
              <Button className="dropdown-button">
                {getStateDisplayName(selectedState)} <DownOutlined />
              </Button>
            </Dropdown>
          </div>
          
          <div className="option-item">
            <span className="option-label">地址类型：</span>
            <Dropdown overlay={addressTypeMenu} trigger={['click']}>
              <Button className="dropdown-button">
                {getAddressTypeDisplayName(selectedAddressType)} <DownOutlined />
              </Button>
            </Dropdown>
          </div>
          
          <div className="option-item">
            <span className="option-label">需要BOI申报：</span>
            <Radio.Group 
              options={[
                { label: '是', value: true },
                { label: '否', value: false },
              ]}
              optionType="button"
              buttonStyle="solid"
              value={needBoi}
              onChange={(e) => setNeedBoi(e.target.value)}
            />
          </div>
          
          <div className="option-item">
            <span className="option-label">需要纸质印章：</span>
            <Radio.Group 
              options={[
                { label: '是', value: true },
                { label: '否', value: false },
              ]}
              optionType="button"
              buttonStyle="solid"
              value={needSeal}
              onChange={(e) => setNeedSeal(e.target.value)}
            />
          </div>
        </div>
        
        <div className="calculator-result">
          <Card className="price-card">
            <Title level={4}>费用总计</Title>
            <div className="price-amount">
              <span className="currency">$</span>
              <span className="amount">{totalPrice}</span>
            </div>
            <Text type="secondary">包含全套公司注册服务</Text>
          </Card>
        </div>
      </div>
    </Card>
  );
};

export default Calculator;
