import React, { useState } from 'react';
import { Layout, Menu, Divider, Button, Dropdown, Avatar } from 'antd';
import {
  HomeOutlined,
  AppstoreOutlined,
  QuestionCircleOutlined,
  UserOutlined,
  SettingOutlined,
  LoginOutlined,
  LogoutOutlined,
} from '@ant-design/icons';
import './Sidebar.css';

const { Sider } = Layout;

const Sidebar = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');

  // 模拟登录功能
  const handleLogin = () => {
    setIsLoggedIn(true);
    setUsername('张三');
  };

  // 模拟退出登录
  const handleLogout = () => {
    setIsLoggedIn(false);
    setUsername('');
  };

  const menuItems = [
    {
      key: 'home',
      icon: <HomeOutlined />,
      label: '首页',
    },
    {
      key: 'services',
      icon: <AppstoreOutlined />,
      label: '业务办理',
    },
    {
      key: 'faq',
      icon: <QuestionCircleOutlined />,
      label: '百问百答',
    },
  ];

  // 用户菜单
  const userMenu = (
    <Menu>
      {isLoggedIn ? (
        <>
          <Menu.Item key="account" icon={<UserOutlined />}>账号信息</Menu.Item>
          <Menu.Item key="settings" icon={<SettingOutlined />}>设置</Menu.Item>
          <Menu.Divider />
          <Menu.Item key="logout" icon={<LogoutOutlined />} onClick={handleLogout}>退出登录</Menu.Item>
        </>
      ) : (
        <Menu.Item key="login" icon={<LoginOutlined />} onClick={handleLogin}>登录</Menu.Item>
      )}
    </Menu>
  );

  return (
    <Sider width={180} className="sidebar" collapsible collapsed={false}>
      <div className="logo">
        <img 
          src="/logo.png" 
          alt="北美生活" 
          className="company-logo" 
        />
      </div>
      <Divider style={{ margin: '0 0 16px 0' }} />
      <Menu
        mode="vertical"
        items={menuItems}
        defaultSelectedKeys={['home']}
        className="sidebar-menu"
      />
      <div className="user-menu">
        <Dropdown overlay={userMenu} placement="topCenter" trigger={['click']}>
          {isLoggedIn ? (
            <div className="user-info">
              <Avatar icon={<UserOutlined />} style={{ backgroundColor: '#1890ff' }} />
              <span className="username">{username}</span>
            </div>
          ) : (
            <Button type="text" icon={<LoginOutlined />} className="login-button">
              登录
            </Button>
          )}
        </Dropdown>
      </div>
      <Divider style={{ margin: '16px 0' }} />
      <div className="company-info">
        <p>© 2024 北美生活</p>
        <p>专业美国公司注册服务</p>
      </div>
    </Sider>
  );
};

export default Sidebar;
