import React, { useState, useEffect } from 'react';
import { Layout } from 'antd';
import Sidebar from './components/Sidebar';
import ChatInterface from './components/ChatInterface';
import Calculator from './components/Calculator';
import CertificatePreview from './components/CertificatePreview';
import './App.css';

// 定义共享状态接口
export interface CompanyInfo {
  state: string;
  companyName: string;
  ownerName: string;
  registrationNumber: string;
  registrationDate: string;
  address: string;
  phone: string;
  email: string;
  businessScope: string;
  companyType: 'CORP' | 'LLC' | 'NONPROFIT';
}

const App = () => {
  // 创建共享状态
  const [companyInfo, setCompanyInfo] = useState({
    state: 'Colorado',
    companyName: '示例公司名称 LLC',
    ownerName: '张三',
    registrationNumber: 'L123456789',
    registrationDate: '2024年3月30日',
    address: '123 Main St, Denver, CO 80202',
    phone: '',
    email: '',
    businessScope: '',
    companyType: 'CORP' as 'CORP' | 'LLC' | 'NONPROFIT'
  });

  // 更新公司信息的函数
  const updateCompanyInfo = (info: Partial<CompanyInfo>) => {
    setCompanyInfo(prevInfo => ({
      ...prevInfo,
      ...info
    }));
  };

  // 确保body类名与侧边栏状态保持一致
  useEffect(() => {
    // 监听侧边栏状态变化
    const observer = new MutationObserver((mutations) => {
      mutations.forEach((mutation) => {
        if (mutation.target instanceof Element) {
          if (mutation.target.classList.contains('collapsed')) {
            document.body.classList.add('sidebar-collapsed');
          } else {
            document.body.classList.remove('sidebar-collapsed');
          }
        }
      });
    });

    const sidebar = document.querySelector('.sidebar');
    if (sidebar) {
      observer.observe(sidebar, { attributes: true, attributeFilter: ['class'] });
    }

    return () => observer.disconnect();
  }, []);

  return (
    <Layout className="app-container">
      <Sidebar />
      <Layout className="main-content">
        <div className="content-container">
          <div className="side-section">
            <ChatInterface />
          </div>
          <div className="chat-section">
            <Calculator companyInfo={companyInfo} updateCompanyInfo={updateCompanyInfo} />
          </div>
          <div className="form-section">
            <CertificatePreview companyInfo={companyInfo} />
          </div>
        </div>
      </Layout>
    </Layout>
  );
};

export default App;
