import React, { useState } from 'react';
import { Layout, Menu, Divider, Button, Dropdown, Avatar } from 'antd';
import type { MenuProps } from 'antd';
import {
  HomeOutlined,
  AppstoreOutlined,
  QuestionCircleOutlined,
  UserOutlined,
  SettingOutlined,
  LoginOutlined,
  LogoutOutlined,
  LeftOutlined,
  RightOutlined,
} from '@ant-design/icons';
import './Sidebar.css';

const { Sider } = Layout;

const Sidebar = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');
  const [collapsed, setCollapsed] = useState(false);

  // 模拟登录功能
  const handleLogin = () => {
    setIsLoggedIn(true);
    setUsername('张三');
  };

  // 模拟退出登录
  const handleLogout = () => {
    setIsLoggedIn(false);
    setUsername('');
  };

  // 切换侧边栏收起/展开状态
  const toggleCollapsed = () => {
    setCollapsed(!collapsed);
    document.body.classList.toggle('sidebar-collapsed', !collapsed);
  };

  // 菜单项点击处理函数
  const handleMenuClick = (key: string) => {
    console.log('Clicked menu item:', key);
    // 这里可以添加路由导航逻辑
  };

  // 非折叠状态的菜单项
  const menuItems = [
    {
      key: 'home',
      icon: <HomeOutlined />,
      label: '首页',
    },
    {
      key: 'services',
      icon: <AppstoreOutlined />,
      label: '业务办理',
    },
    {
      key: 'faq',
      icon: <QuestionCircleOutlined />,
      label: '百问百答',
    },
  ];

  // 折叠状态的菜单项（只显示图标）
  const collapsedMenuItems = [
    {
      key: 'home',
      icon: <HomeOutlined style={{ fontSize: '18px' }} />,
      label: ' ',
      onClick: () => handleMenuClick('home')
    },
    {
      key: 'services',
      icon: <AppstoreOutlined style={{ fontSize: '18px' }} />,
      label: ' ',
      onClick: () => handleMenuClick('services')
    },
    {
      key: 'faq',
      icon: <QuestionCircleOutlined style={{ fontSize: '18px' }} />,
      label: ' ',
      onClick: () => handleMenuClick('faq')
    },
  ];

  // 用户菜单项
  const userMenuItems = [
    ...(isLoggedIn 
      ? [
          { key: 'account', icon: <UserOutlined />, label: '账号信息' },
          { key: 'settings', icon: <SettingOutlined />, label: '设置' },
          { type: 'divider' },
          { key: 'logout', icon: <LogoutOutlined />, label: '退出登录', onClick: handleLogout }
        ]
      : [
          { key: 'login', icon: <LoginOutlined />, label: '登录', onClick: handleLogin }
        ])
  ];

  return (
    <div className="sidebar-container">
      <Sider 
        width={180} 
        className={`sidebar ${collapsed ? 'collapsed' : ''}`} 
        collapsed={collapsed}
        collapsedWidth={60}
        trigger={null}
      >
        <div className="logo">
          <img 
            src="/logo.png" 
            alt="北美生活" 
            className="company-logo" 
          />
        </div>
        {!collapsed && <Divider style={{ margin: '0 0 16px 0' }} />}

        {/* 根据是否折叠选择不同的菜单项 */}
        {collapsed ? (
          <div className="collapsed-menu">
            {collapsedMenuItems.map(item => (
              <div 
                key={item.key} 
                className="collapsed-menu-item"
                onClick={item.onClick}
              >
                {item.icon}
              </div>
            ))}
          </div>
        ) : (
          <Menu
            mode="inline"
            items={menuItems}
            defaultSelectedKeys={['home']}
            className="sidebar-menu"
            onClick={({key}) => handleMenuClick(key)}
          />
        )}

        <div className="user-menu">
          <Dropdown menu={{ items: userMenuItems }} placement="topCenter" trigger={['click']}>
            {isLoggedIn ? (
              <div className="user-info">
                <Avatar icon={<UserOutlined />} style={{ backgroundColor: '#1890ff' }} />
                <span className="username">{username}</span>
              </div>
            ) : (
              <Button type="text" icon={<LoginOutlined />} className="login-button">
                {!collapsed && '登录'}
              </Button>
            )}
          </Dropdown>
        </div>
        {!collapsed && (
          <div className="company-info">
            <p>© 2024 北美生活</p>
            <p>专业美国公司注册服务</p>
          </div>
        )}
      </Sider>
      <div 
        className="collapse-button" 
        onClick={toggleCollapsed} 
        style={{ 
          left: collapsed ? '50px' : '170px' 
        }}
      >
        {collapsed ? <RightOutlined style={{ fontSize: '12px' }} /> : <LeftOutlined style={{ fontSize: '12px' }} />}
      </div>
    </div>
  );
};

export default Sidebar;
