import React, { useState, useEffect } from 'react';
import { Layout, Menu, Divider, Button, Dropdown, Avatar } from 'antd';
import type { MenuProps } from 'antd';
import {
  HomeOutlined,
  AppstoreOutlined,
  QuestionCircleOutlined,
  UserOutlined,
  SettingOutlined,
  LoginOutlined,
  LogoutOutlined,
  LeftOutlined,
  RightOutlined,
} from '@ant-design/icons';
import './Sidebar.css';

const { Sider } = Layout;

const Sidebar = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');
  const [collapsed, setCollapsed] = useState(false);

  // 添加useEffect来监听窗口大小变化
  useEffect(() => {
    // 确保所有样式加载完毕
    setTimeout(() => {
      window.dispatchEvent(new Event('resize'));
    }, 200);
  }, [collapsed]);

  // 模拟登录功能
  const handleLogin = () => {
    setIsLoggedIn(true);
    setUsername('张三');
  };

  // 模拟退出登录
  const handleLogout = () => {
    setIsLoggedIn(false);
    setUsername('');
  };

  // 切换侧边栏收起/展开状态
  const toggleCollapsed = () => {
    const newCollapsed = !collapsed;
    setCollapsed(newCollapsed);
    document.body.classList.toggle('sidebar-collapsed', newCollapsed);

    // 触发窗口resize事件，帮助Antd组件自适应
    setTimeout(() => {
      window.dispatchEvent(new Event('resize'));
    }, 300);
  };

  const menuItems = [
    {
      key: 'home',
      icon: <HomeOutlined style={{ fontSize: collapsed ? 18 : 16 }} />,
      label: '首页',
    },
    {
      key: 'services',
      icon: <AppstoreOutlined style={{ fontSize: collapsed ? 18 : 16 }} />,
      label: '业务办理',
    },
    {
      key: 'faq',
      icon: <QuestionCircleOutlined style={{ fontSize: collapsed ? 18 : 16 }} />,
      label: '百问百答',
    },
  ];

  // 用户菜单项
  const userMenuItems = [
    ...(isLoggedIn 
      ? [
          { key: 'account', icon: <UserOutlined />, label: '账号信息' },
          { key: 'settings', icon: <SettingOutlined />, label: '设置' },
          { type: 'divider' },
          { key: 'logout', icon: <LogoutOutlined />, label: '退出登录', onClick: handleLogout }
        ]
      : [
          { key: 'login', icon: <LoginOutlined />, label: '登录', onClick: handleLogin }
        ])
  ];

  return (
    <div className="sidebar-container">
      <Sider 
        width={180} 
        className={`sidebar ${collapsed ? 'collapsed' : ''}`} 
        collapsed={collapsed}
        collapsedWidth={60}
        trigger={null}
      >
        <div className="logo">
          <img 
            src="/logo.png" 
            alt="北美生活" 
            className="company-logo" 
          />
        </div>
        {!collapsed && <Divider style={{ margin: '0 0 16px 0' }} />}
        <Menu
          mode="inline"
          items={menuItems}
          defaultSelectedKeys={['home']}
          className="sidebar-menu"
          inlineCollapsed={collapsed}
        />
        <div className="user-menu">
          <Dropdown menu={{ items: userMenuItems }} placement="topCenter" trigger={['click']}>
            {isLoggedIn ? (
              <div className="user-info">
                <Avatar icon={<UserOutlined />} style={{ backgroundColor: '#1890ff' }} />
                <span className="username">{username}</span>
              </div>
            ) : (
              <Button type="text" icon={<LoginOutlined />} className="login-button">
                {!collapsed && '登录'}
              </Button>
            )}
          </Dropdown>
        </div>
        {!collapsed && (
          <div className="company-info">
            <p>© 2024 北美生活</p>
            <p>专业美国公司注册服务</p>
          </div>
        )}
      </Sider>
      <div 
        className="collapse-button" 
        onClick={toggleCollapsed} 
        style={{ 
          left: collapsed ? '50px' : '170px' 
        }}
      >
        {collapsed ? <RightOutlined style={{ fontSize: '12px' }} /> : <LeftOutlined style={{ fontSize: '12px' }} />}
      </div>
    </div>
  );
};

export default Sidebar;
