import React from 'react';
import { Input, Button, Avatar } from 'antd';
import { SendOutlined, UserOutlined } from '@ant-design/icons';
import './ChatInterface.css';

interface Message {
  id: number;
  content: string;
  type: 'user' | 'ai';
  timestamp: Date;
}

export default function ChatInterface() {
  const [messages, setMessages] = React.useState([
    {
      id: 1,
      content: '您好！欢迎访问美美生活！我们专注于为中国客户提供美国公司注册与税务申报服务。请问有什么可以帮助您的吗？',
      type: 'ai',
      timestamp: new Date(),
    },
  ]);
  const [inputValue, setInputValue] = React.useState('');
  const messagesEndRef = React.useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  React.useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSend = () => {
    if (inputValue.trim() === '') return;

    // 添加用户消息
    const userMessage = {
      id: messages.length + 1,
      content: inputValue,
      type: 'user',
      timestamp: new Date(),
    };

    setMessages((prev) => [...prev, userMessage]);
    setInputValue('');

    // 模拟AI回复
    setTimeout(() => {
      const aiResponse = {
        id: messages.length + 2,
        content: getAIResponse(inputValue),
        type: 'ai',
        timestamp: new Date(),
      };
      setMessages((prev) => [...prev, aiResponse]);
    }, 1000);
  };

  const getAIResponse = (query) => {
    // 简单的模拟回复，实际应用中应该连接到真实的AI服务
    const responses = {
      'default': '感谢您的咨询。我们专注于为中国客户提供美国公司注册与税务申报服务。请问您有具体的问题吗？',
      '费用': '我们的基础注册套餐从$299起，具体费用取决于注册州、地址类型等因素。您可以使用上方的计算器获取精确报价。',
      '时间': '一般情况下，美国公司注册流程需要7-10个工作日完成。如需加急服务，请联系我们的客服。',
      '流程': '注册流程包括：选择公司类型、确定注册州、提交申请材料、获取EIN税号、开设银行账户等。我们将全程协助您完成这些步骤。',
      '材料': '您需要提供有效身份证件、联系方式等基本信息。对于特定行业可能需要额外资质文件。',
      '税务': '美国公司需要按时进行税务申报，包括联邦税、州税和地方税。我们提供全面的税务咨询和申报服务。',
    };

    for (const key in responses) {
      if (query.includes(key)) {
        return responses[key];
      }
    }

    return responses['default'];
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSend();
    }
  };

  return (
    <div className="chat-interface">
      <div className="chat-history">
        {messages.map((message) => (
          <div
            key={message.id}
            className={`message ${message.type === 'user' ? 'user-message' : 'ai-message'}`}
          >
            <div className="message-avatar">
              {message.type === 'user' ? (
                <Avatar icon={<UserOutlined />} />
              ) : (
                <Avatar style={{ backgroundColor: '#1a73e8' }}>AI</Avatar>
              )}
            </div>
            <div className="message-content">
              <div className="message-bubble">{message.content}</div>
              <div className="message-time">
                {message.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
              </div>
            </div>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>
      <div className="chat-input">
        <Input
          placeholder="请输入您的问题..."
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          onKeyPress={handleKeyPress}
          suffix={
            <Button
              type="primary"
              shape="circle"
              icon={<SendOutlined />}
              onClick={handleSend}
              disabled={inputValue.trim() === ''}
            />
          }
        />
      </div>
    </div>
  );
}
