import React from 'react';
import './CertificatePreview.css';

interface CompanyInfo {
  state?: string;
  companyName?: string;
  ownerName?: string;
  registrationNumber?: string;
  registrationDate?: string;
  address?: string;
  phone?: string;
  email?: string;
  businessScope?: string;
  companyType?: string;
}

// 生成ID号码的函数
const generateIdNumber = () => {
  return `2025${Math.floor(10000000 + Math.random() * 90000000)}`;
};

// 生成文档号码的函数
const generateDocumentNumber = () => {
  return `2025${Math.floor(10000 + Math.random() * 90000)}`;
};

// 格式化当前日期和时间
const getFormattedDateTime = () => {
  const now = new Date();
  const month = String(now.getMonth() + 1).padStart(2, '0');
  const day = String(now.getDate()).padStart(2, '0');
  const hours = String(now.getHours() % 12 || 12).padStart(2, '0');
  const minutes = String(now.getMinutes()).padStart(2, '0');
  const seconds = String(now.getSeconds()).padStart(2, '0');
  const ampm = now.getHours() >= 12 ? 'PM' : 'AM';
  
  return `07/02/2024 09:38:31 PM`;
};

// 生成金额
const generateAmount = () => {
  return `$${(50 + Math.random() * 50).toFixed(2)}`;
};

interface CertificatePreviewProps {
  companyInfo: CompanyInfo;
}

const CertificatePreview = ({ companyInfo }: CertificatePreviewProps) => {
  // 确保所有必要的信息都存在，否则使用占位符
  const {
    state = 'Colorado',
    companyName = 'Sample Company Name',
    ownerName = 'John Doe',
    address = '123 Business Street, Denver, CO 80201',
    businessScope = 'General Business Services',
    companyType = 'LLC'
  } = companyInfo;

  // 对于每次渲染保持一致的ID和日期
  const idNumber = React.useMemo(() => generateIdNumber(), []);
  const documentNumber = React.useMemo(() => generateDocumentNumber(), []);
  const filedDate = React.useMemo(() => getFormattedDateTime(), []);
  const paidAmount = React.useMemo(() => generateAmount(), []);

  return (
    <div className="certificate-preview">
      <div className="colorado-certificate">
        <div className="certificate-header">
          <div className="colorado-title">
            <h2>Department of State</h2>
            <h2>Certificate of {companyType} Registration</h2>
          </div>
          <div className="right-info">
            <div className="id-line id-header">
              <span>Colorado Secretary of State</span>
            </div>
            <div className="id-line">
              <span>ID#: {idNumber}</span>
            </div>
            <div className="id-line">
              <span>Document #: {documentNumber}</span>
            </div>
            <div className="id-line">
              <span>Filed on: {filedDate}</span>
            </div>
            <div className="id-line">
              <span>Paid: {paidAmount}</span>
            </div>
          </div>
        </div>

        <div className="colorado-info-box">
          <div className="colorado-info-item">
            <span>This "Certificate of {companyType} Registration" is hereby issued pursuant to the laws of the State of Colorado.</span>
          </div>
        </div>

        <div className="colorado-document-title">
          <h1>{companyType === 'LLC' ? 'Articles of Organization' : 'Articles of Incorporation'}</h1>
        </div>

        <div className="colorado-content">
          <div className="colorado-content-item">
            <div className="content-label">The name of the {companyType.toLowerCase()} is:</div>
            <div className="content-value company-name">{companyName}</div>
          </div>

          <div className="colorado-content-item">
            <div className="content-label">The principal office address of the {companyType.toLowerCase()} is:</div>
            <div className="content-value address-value">{address}</div>
          </div>

          <div className="colorado-content-item">
            <div className="content-label">The registered agent name and registered agent address of the {companyType.toLowerCase()} is:</div>
            <div className="content-value address-value">{ownerName}<br />{address}</div>
          </div>

          <div className="colorado-content-item">
            <div className="content-label">The name and address of the person forming the {companyType.toLowerCase()} is:</div>
            <div className="content-value incorporator-value">{ownerName}<br />{address}</div>
          </div>
        </div>

        <div className="colorado-disclaimer">
          <p>The {companyType} has been duly organized in accordance with the laws of the State of Colorado. The registration is effective from the date of filing and shall remain in effect until further notification.</p>
          <p>In Testimony Whereof, I have hereunto set my hand and affixed the seal of the State of Colorado.</p>
        </div>

        <div className="colorado-official">
          <div className="signature-line"></div>
          <div className="official-name">Secretary of State</div>
          <div className="official-state">State of Colorado</div>
        </div>
      </div>
    </div>
  );
}

export default CertificatePreview;
